window.onload=function(){let e=document.querySelectorAll('a[href^="#"]');for(let t=0;t<e.length;t++)e[t].addEventListener("click",(function(o){let n=e[t].getAttribute("href"),l=document.getElementById(n.replace("#",""));setTimeout((function(){l.scrollIntoView({block:"start",behavior:"smooth"})}),500),o.preventDefault()}))};